const pontuacao=document.querySelector("#pontuacao");const mensagem=document.querySelector("#mensagem");const formulario=document.querySelector("#formulario");const numero=document.querySelector("#numero");const par=document.querySelector("#par");const impar=document.querySelector("#impar");const botaoJogar=document.querySelector("#botao-jogar");const anoAtual=document.querySelector("#ano-atual");let pontos=0;const carregarAnoAtual=()=>{const dataAtual=new Date();anoAtual.innerText=dataAtual.getFullYear()};const exibirMensagem=(textoMensagem,classe)=>{mensagem.classList.remove("sucesso","falha");mensagem.innerText=textoMensagem;mensagem.classList.add(classe)};const limparCampoNumero=()=>{numero.value="";if(window.screen.width<1024)numero.blur();else numero.focus()};const resetarTextoBotaoJogar=()=>(botaoJogar.value="Jogar");const incrementarPontuacao=()=>{pontos++;pontuacao.innerText=pontos};const reiniciarJogo=()=>{pontos=0;pontuacao.innerText=pontos;par.checked=!0;botaoJogar.value="Jogar Novamente"};const definirVencedor=(valorNumero)=>{const numeroComputador=Math.floor(Math.random()*6);const resultado=(Number(valorNumero)+numeroComputador)%2;if((par.checked&&resultado===0)||(impar.checked&&resultado!==0)){exibirMensagem("Você venceu!","sucesso");incrementarPontuacao()}else{exibirMensagem("Você perdeu!","falha");reiniciarJogo()}
limparCampoNumero()};const validarDados=(event)=>{event.preventDefault();const valorNumero=numero.value.trim();if(!numero.checkValidity()||!valorNumero||isNaN(valorNumero)||!Number.isInteger(Number(valorNumero))||valorNumero<0||valorNumero>5){exibirMensagem("Erro: Informe um número inteiro entre 0 e 5!","falha");limparCampoNumero();return}
if(!par.checked&&!impar.checked){exibirMensagem("Erro: Selecione uma opção!","falha");return}
resetarTextoBotaoJogar();definirVencedor(valorNumero)};document.addEventListener("DOMContentLoaded",()=>carregarAnoAtual());formulario.addEventListener("submit",(event)=>validarDados(event))